import React, { useState } from 'react';
import BookItem from '../bookItem/bookItem';
import books from './books.json';
import './books-list.css';

function BooksList() {
    const [searchWord, setSearchWord] = useState('')
    const [arrBooks, setArrBooks] = useState(books.books)


    const filter = (event) => {
        setSearchWord(event.target.value);

        if (event.target.value.length === 0) {
            setArrBooks(books.books)
        } else {
            setArrBooks(
                books.books.filter((element) => {
                    if (element.title.toLowerCase().includes(searchWord.toLowerCase())) {
                        return true
                    } else {
                        return false
                    }
                }
                )
            )
        }
    }

    function checkValues(event) {
        console.dir(event.target.value)
        switch (event.target.value) {
            case '15': setArrBooks(
                books.books.filter((element) => {
                    if (element.price > 15) {
                        return true
                    } else {
                        return false
                    }
                }
                )
            );
                break
            case '15-30':
            case '30':
                alert("This is " + selectedValue + " and " + radioValue + ". Do some conditional logic with the values!");
                break;
            default:
                break;
        }
    }


    if (!localStorage.authorization) {
        document.location = '/'
    } else {
        return (
            <main>
                <section>
                    <div className="book-search">
                        <input
                            onChange={(event) => filter(event)}
                            placeholder="Search by book name" />
                        <div className="form-select">
                            <fieldset>
                                <select
                                    className='select'
                                    name="speed"
                                    placeholder='Price'
                                    onChange={(event) => checkValues(event)}
                                    id="speed">
                                    <option selected disabled>Price</option>
                                    <option value='15'>less than 15$</option>
                                    <option value='15-30'>15 - 30$</option>
                                    <option value='30'>more than 30$</option>
                                </select>
                            </fieldset>
                        </div>
                    </div>
                    <div className="main">
                        <BookItem books={arrBooks} />

                    </div>
                </section>
            </main>
        )
    }

}

export default BooksList;
