import React from 'react';
import './books-list.css';



function BooksList() {
    const req = async url => {
        const data = await fetch(url);
        return data.json()
    };

    function show(data) {
        console.dir(data)
        const bookList = Array.isArray(data.books) ?
            data.books.map((el) => {
                return `<div class="book-card"><img src=${el.image != "" ? el.image : "../assets/imageNotFound.png"} alt=${el.title}>
            <div class="book-description">
                <p><span>Book name:</span> ${el.title}</p>
                <p><span>Book autor:</span> ${el.author}</p>
            </div>
            <div class="price-calculate">
                <div>Prise, $ <span>${el.price}</span></div>
                <button type="button">View</button>
            </div>
            </div>`
        }).join("")
            : ""

        document.querySelector(".main-section").insertAdjacentHTML("beforeend", bookList)
    };

    req('./books.json').then(d => show(d))
                    .catch(info => console.error(info))
    return (
        <main>
            <section>
                <div className="book-search">
                    <input placeholder="Search by book name" />
                    <div className="form-select">
                        <fieldset>
                            <select className='select' name="speed" id="speed">
                                <option value='1'>Rating</option>
                                <option value='2'>Price</option>
                            </select>
                        </fieldset>
                    </div>
                </div>
                <div className="main">
                </div>
            </section>
        </main>
    )
}

export default BooksList;
