import React, {useState} from 'react';
import './counter.css';

function Counter() {
const [quantity, setQuantity] = useState(1),
addQuantity = (event) => {
    if (event.target.className == 'up' || event.target.parentElement.className == 'up') {
        setQuantity(+quantity + 1)
    } else if (event.target.className == 'up' || event.target.parentElement.className == 'up') {
        setQuantity(+quantity + 1)
    } 
    console.dir(event.target);
}

    return (
        <div className="price-calculate">
            <div>Prise, $ <span>52.72</span></div>
            <p>
                <label htmlFor="spinner">Count</label>
                <input 
                type='number' 
                id="spinner" 
                value={quantity} 
                onChange={event => setQuantity(event.target.value)} />
                <button 
                className='up'
                onClick={addQuantity}><div></div></button>
                <button 
                onClick={() => setQuantity(+quantity - 1)}
                className='down'><div></div></button>
            </p>
            <div id="total">Total price <span>{(quantity * 52.72).toFixed(2)}</span></div>
            <button className='add-cart' type="button">Add to cart</button>
        </div>

    )
}

export default Counter;
