 const BookItem = ({books}) => {
    return (
        <div className="book-cards"> 
            {books.map((el) => {
                <div className="book-card" key={el.id}>
                    <img src={el.image != "" ? el.image : "../assets/imageNotFound.png"} alt={el.title}/>
                    <div className="book-description">
                        <p><span>Book name:</span> {el.title}</p>
                        <p><span>Book autor:</span> {el.author}</p>
                    </div>
                    <div className="price-calculate">
                        <div>Prise, $ <span>{el.price}</span></div>
                        <button type="button">View</button>
                    </div>
                </div>
            })}
        </div>
    )
}

export default BookItem;
