import React from 'react';
// import './select.css';
// import $ from "jquery";

class Chosen extends React.Component {
    componentDidMount() {
      this.$el = $(this.el);
      this.$el.chosen();
    }

    componentWillUnmount() {
      this.$el.chosen('destroy');
    }

    render() {
      return (
        <div>
          <select ref={el => this.el = el}>
            {this.props.children}
          </select>
        </div>
      );
    }
  }

  export default Chosen;
