import React, { useEffect, useState } from 'react';
import BookItem from '../bookItem/bookItem';
import books from './books.json'
import './books-list.css';

function BooksList() {
    const [searchWord, setSearchWord] = useState('')
    const [arrBooks, setArrBooks] = useState(books.books)


    let allBooks = {
        booksItems: arrBooks,
        searchWord: searchWord
    }

    const filter = () => {

        setArrBooks(books.books.filter((element) => {
               if (element.title.toLowerCase().includes(allBooks.searchWord.toLowerCase())) {
                return true
               } else {
                return false
               }
            }
            ))

    console.dir(arrBooks);        
    console.dir(allBooks.booksItems);  

    }



    if (!localStorage.authorization) {
        document.location = '/'
    } else {
        return (
            <main>
                <section>
                    <div className="book-search">
                        <input
                            onChange={filter}
                            placeholder="Search by book name" />
                        <div className="form-select">
                            <fieldset>
                                <select className='select' name="speed" id="speed">
                                    <option value='1'>Rating</option>
                                    <option value='2'>Price</option>
                                </select>
                            </fieldset>
                        </div>
                    </div>
                    <div className="main">
                        <BookItem books={allBooks.booksItems} />

                    </div>
                </section>
            </main>
        )
    }

}

export default BooksList;
