import React, {useEffect, useState} from 'react';
import './fullCart.css';
import ButtonPurchase from '../buttonPurchase/buttonPurchase';

function FullCart() {
    const [selectedBooks, setSelectedBooks] = useState(JSON.parse(localStorage.getItem('selectedBooks')));
    useEffect(() => {
        const raw = localStorage.getItem('selectedBooks');
        setSelectedBooks(JSON.parse(raw));
    }, [])
    if (!localStorage.authorization) {
        document.location = '/'
    } else {
        {selectedBooks.map((element) => console.log(element))}
        return (
            <div className="cart-container">
                <ButtonPurchase />
                <div className='products'>
                    <div className='products__one'>
                        <div className='book-nane'>
                            {console.log(selectedBooks)}
                        </div>
                        <div className='count'>
                            Count
                        </div>
                        <div className='price'>
                            Total price
                        </div>
                    </div>

                </div>
                <div className='total'>
                    Total price, $ 103
                </div>
            </div>

        )
    }
}

export default FullCart;
