import React from 'react';
import './specific-book.css';
import notFound from '../../assets/imageNotFound.png';
import Counter from '../counter/counter';

function SpecificBook() {
    const book = JSON.parse(localStorage.getItem('viewBook'))

    if (!localStorage.authorization) {
        document.location = '/'
    } else {
        return (
            <main>
                <section>
                    <div className="main-section">
                        <img src={book.image} alt={book.title} />
                        <div className="book-description">
                            <p><span>Book name:</span> </p>
                            <p><span>Book autor:</span> Marijn Haverbeke</p>
                            <p><span>Book level:</span> beginer</p>
                            <p><span>Book tags:</span> core</p>
                        </div>
                        <Counter />
                    </div>
                    <p className="long-description"><span>Desciption:</span> A book providing an introduction to the JavaScript language and programming in
                        general Lorem ipsum dolor sit amet consectetur adipisicing elit. Natus explicabo sapiente placeat amet, exercitationem repellendus ea nisi modi? Autem consequuntur maxime repudiandae ut cumque, cupiditate in assumenda nam facilis labore.</p>
                </section>
            </main>
        )
    }
}

export default SpecificBook;
