import React, { useEffect, useState } from 'react';
import './fullCart.css';
import ButtonPurchase from '../buttonPurchase/buttonPurchase';

function FullCart() {
    const [selectedBooks, setSelectedBooks] = useState(JSON.parse(localStorage.getItem('selectedBooks')));

    useEffect(() => {
        const raw = localStorage.getItem('selectedBooks');
        setSelectedBooks(JSON.parse(raw));
        document.querySelector('.button button').removeAttribute('disabled')
    }, []);

    const totalPrice = selectedBooks.reduce((acc, selectedBooks) => {
        return acc + selectedBooks.price * selectedBooks.quantity
    }, 0);

    if (!localStorage.authorization) {
        document.location = '/'
    } else {
        if (!localStorage.selectedBooks) {
            document.location = '/purchase'
        } else {
         return (
            <div className="cart-container">
                <ButtonPurchase />
                <div className='products'>
                    {selectedBooks.map((element) => {
                        return (
                            <div className='products__one' key={element.id}>
                                <div className='book-title'>
                                    {element.title}
                                </div>
                                <div className='book-price'>
                                    {element.price}$
                                </div>
                                <div className='count'>
                                {element.quantity}
                                </div>
                                <div className='price'>
                                {element.price * element.quantity}$
                                </div>
                            </div>
                        )
                    })}
                </div>
                <div className='total'>
                    Total price, $ {totalPrice}
                </div>
            </div>

        )   
        }

    }
}

export default FullCart;
